import { View, Text, Pressable, StyleSheet, TextInput } from "react-native";
import { useEffect } from "react";
import { MaterialIcons } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';

const LoginScreen = ({navigation}) => {
    useEffect(() => {
        navigation.setOptions({
            headerStyle: {
                height: 50,
                backgroundColor: 'blueviolet',
            },
            headerTitleStyle: {
                color: 'white'
            },
            headerTitleAlign: 'center',
            headerLeft: () => {
                return (
                    <Pressable style={styles.btnBack}>
                        <MaterialIcons name="arrow-back-ios" size={20} color="white" />
                    </Pressable>
                )
            }
        })

        
    }, [])

    return (
        <View style={styles.container}>
            <View style={styles.handWave}>
                <MaterialCommunityIcons name="hand-wave" size={50} color="black" />
            </View>
            <Text style={styles.txtLogin}>Login to your account</Text>
            <Text style={styles.txt}>
                Login to add items to your wishlist, write to sellers, and view your orders
                {'\n'}
            </Text>
            <Text style={styles.txtPhoneNum}>
                Enter your phone number <Text style={styles.asterix}>*</Text>
            </Text>
            <TextInput
                style={styles.txtInput}
                placeholder="+380 (__) ___-__-__"
                keyboardType="numeric"
            />
            <Pressable style={styles.btnLogin}
                onPress={() => {}}
            >
                <Text>  Login</Text>
            </Pressable>
        </View>
    );
}

export default LoginScreen;

const styles = StyleSheet.create({
    container: {
        alignItems: 'flex-start',
        paddingHorizontal: 25
    },
    btnBack: {
        paddingLeft: 18
    },
    handWave: {
        width: '100%',
        alignItems: 'center',
        paddingTop: 26
    },
    txtLogin: {
        paddingTop: 20,
        fontSize: 20,
        fontWeight: 'bold'
    },
    txt: {
        width: '95%',
        paddingVertical: 13,
        fontSize: 15
    },
    txtPhoneNum: {
        paddingVertical: 7,
        fontSize: 15
    },
    asterix: {
        fontSize: 18,
        fontWeight: 500,
        color: 'red'
    },
    txtInput: {
        width: '100%',
        height: 37,
        borderWidth: 1,
        borderRadius: 8,
        borderColor: 'silver',
        paddingLeft: 15,
        paddingVertical: 0,
        justifyContent: 'center',
        alignItems: 'center'
    },
    btnLogin: {
        width: '100%',
        marginTop: 14,
        backgroundColor: 'blueviolet'
    }
});
